import axios from 'axios';
import React, { Component } from 'react';
import './container.css';
//import { ReactComponent } from "*.svg";
import './maincontainer.css';
import './operations.css';
import Part from "./part";
import Picture from "./picture2";


class Maincontainer extends Component{
  state = {
    response: '',
    post: '',
    responseToPost: '',
  };

  componentDidMount() {
    this.callApi()
      .then(res => this.setState({ response: res.express }))
      .catch(err => console.log(err));
  }

  callApi = async () => {
    const response = await fetch('/backEnd/hello');
    const body = await response.json();

    if (response.status !== 200) throw Error(body.message);

    return body;
  };
  handleSubmit = async e => {
    e.preventDefault();
    const response = await fetch('/backEnd/hello', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ post: this.state.post }),
    });
    const body = await response.text();

    this.setState({ responseToPost: body });
  };
    render(){
        return(
            <div className='Maincontainer'><h1>Bank Management</h1>
        <Picture></Picture>
        <div className='Operations'>
        <div className='Container'><h3>Remove Bank</h3>
        <Part label="Bank name"></Part>
       <br/>
        <button>Remove </button>

        <div className='Container'><h3>Add Bank</h3>
        <Part label="Bank name"></Part>
        <input type='text' onChange={this.onChangePersonName}></input>
        <Part label="Interest Rate"></Part><br/>
        <button onClick={this.onSubmit}>Add</button></div></div></div>);
        <p>{this.state.response}</p>
        <form onSubmit={this.handleSubmit}>
          <p>
            <strong>Post to Server:</strong>
          </p>
          <input
            type="text"
            value={this.state.post}
            onChange={e => this.setState({ post: e.target.value })}
          />
          <button type="submit">Submit</button>
        </form>
        <p>{this.state.responseToPost}</p></div>)
    }
}

export default Maincontainer;
