import './maincontainer.css';
import Picture from './picture';
import Part2 from './part2';
import Part3 from './part3';
import './part3.css';
class Maincontainer extends Component{
    state={bank:[{name:'boc',rate:8,amount:''},{name:'Sampath',rate:12,amount:''},{name:'dfcc',rate:10,amount:''}],
money:'',
time:''};
submitHandler= (array)=> {

    array.amount=((this.state.rate)/1200)*this.state.money*this.state.time;
    console.log(array.amount);
  };
  handleMoneyChange= (e) =>{
    this.state.money=e.target.value};

    handleTimeChange= (e) =>{
       this.state.time=e.target.value
    //    console.log(e.target.value);
}


        render(){
            return(
        <div className='Maincontainer'>Bank Investment
        <Picture></Picture>
        <Part label='Amount' change={this.handleMoneyChange}></Part>
        <p>Investment Period</p>
    <Part3 change2={this.handleTimeChange}></Part3><br/>
    <button onClick={this.submitHandler(this.state.bank)}>submit</button>
        </div>);
    }
}
export default Maincontainer;
